mapper.insert:

	SELECT board_seq.nextVal FROM dual

INSERT INTO board (
	board_no, board_title, board_write_date, board_contents, member_no
) VALUES(
	#{boardNo}, #{boardTitle}, sysdate, #{boardContent}, #{memberNo}
)

mapper.update:
UPDATE BOARD SET
boardTitle = #{boardTitle}
, boardContent = #{boardContent}
, boardWriteDate = #{boardWriteDate}
WHERE board_no = #{boardNo}

